CREATE TRIGGER `trg_UpdateItemAvailability_PO` AFTER INSERT ON `transactions` FOR EACH ROW BEGIN
    -- Procesar tipo de transacción 'PO' (Purchase Order)
    IF NEW.Type = 'PO' THEN
        -- Actualizar la cantidad en el almacén de destino
        UPDATE item_availabilities
        SET Quantity = Quantity + NEW.Quantity
        WHERE Product_ID = NEW.Product_ID
          AND Warehouse_ID = NEW.Destination_ID;

        -- Verificar si se actualizó alguna fila
        IF ROW_COUNT() = 0 THEN
            -- Si no se actualizó ninguna fila, insertar una nueva entrada
            INSERT INTO item_availabilities (Product_ID, Warehouse_ID, Quantity)
            VALUES (NEW.Product_ID, NEW.Destination_ID, NEW.Quantity);
        END IF;
    END IF;
    
    -- Procesar tipo de transacción 'TRF' (Transfers Order)
    IF NEW.Type = 'TRF' THEN
        -- Actualizar la cantidad en el almacén de destino
        UPDATE item_availabilities
        SET Quantity = Quantity - NEW.Quantity
        WHERE Product_ID = NEW.Product_ID
          AND Warehouse_ID = NEW.Origin_ID;
          
        UPDATE item_availabilities
        SET Quantity = Quantity + NEW.Quantity
        WHERE Product_ID = NEW.Product_ID
          AND Warehouse_ID = NEW.Destination_ID;

        -- Verificar si se actualizó alguna fila
        IF ROW_COUNT() = 0 THEN
            -- Si no se actualizó ninguna fila, insertar una nueva entrada
            INSERT INTO item_availabilities (Product_ID, Warehouse_ID, Quantity)
            VALUES (NEW.Product_ID, NEW.Destination_ID, NEW.Quantity);
        END IF;
    END IF;
    
    
    
    
    
    
END
$$
DELIMITER ;

